  CREATE OR REPLACE FORCE VIEW "SMRGAA"."SMRGAA_V_ALLEVAMENTI" ("CODICE_AZIENDA_ZOOTECNICA", "ID_SPECIE_ANIMALE", "DESCRIZIONE_SPECIE_ANIMALE", "UNITA_MISURA_SPECIE", "DENOMINAZIONE_ALLEVAMENTO", "CODICE_FISCALE_DETENTORE", "DENOMINAZIONE_DETENTORE", "CODICE_FISCALE_PROPRIETARIO", "DENOMINAZIONE_PROPRIETARIO", "DATA_APERTURA_ALLEVAMENTO", "DATA_INIZIO_DETENZIONE", "DATA_FINE_DETENZIONE", "FLAG_ASSICURATO_COSMAN", "FLAG_SOCCIDA", "MOTIVO_SOCCIDA", "ID_ALLEVAMENTO", "ID_ASL", "ID_ORIENTAMENTO_PRODUTTIVO", "DESC_ORIENTAMENTO_PRODUTTIVO", "LATITUDINE", "LONGITUDINE", "ID_TIPO_PRODUZIONE", "DESC_TIPO_PRODUZIONE", "DESC_TIPO_PRODUZIONE_COSMAN", "DESCRIZIONE", "NOTE", "TELEFONO", "ISTAT_COMUNE", "INDIRIZZO", "CAP", "ID_UTE", "ID_CATEGORIE_ALLEVAMENTO", "ID_CATEGORIA_ANIMALE", "DESCRIZIONE_CATEGORIA_ANIMALE", "PESO_VIVO_UNITARIO", "ID_SOTTOCATEGORIA_ANIMALE", "DESCRIZIONE_SOTTOCATEG_ANIMALE", "QUANTITA", "QUANTITA_PROPRIETA", "FLAG_BIOLOGICO", "GIORNI_PASCOLO_ESTATE", "GIORNI_PASCOLO_INVERNO", "ORE_PASCOLO_ESTATE", "ORE_PASCOLO_INVERNO", "GIORNI_VUOTO_SANITARIO", "NUMERO_CICLI_ANNUALI", "PESO_VIVO", "ID_DICHIARAZIONE_CONSISTENZA", "ID_SOTTOCATEGORIA_ALLEVAMENTO", "FLAG_PASCOLABILE") AS 
  SELECT A.CODICE_AZIENDA_ZOOTECNICA,
          TSPA.ID_SPECIE_ANIMALE,
          TSPA.DESCRIZIONE AS DESCRIZIONE_SPECIE_ANIMALE,
          TSPA.UNITA_MISURA AS UNITA_MISURA_SPECIE,
          A.DENOMINAZIONE_ALLEVAMENTO,
          A.CODICE_FISCALE_DETENTORE,
          A.DENOMINAZIONE_DETENTORE,
          A.CODICE_FISCALE_PROPRIETARIO,
          A.DENOMINAZIONE_PROPRIETARIO,
          A.DATA_APERTURA_ALLEVAMENTO,
          A.DATA_INIZIO_DETENZIONE,
          A.DATA_FINE_DETENZIONE,
          A.FLAG_ASSICURATO_COSMAN,
          A.FLAG_SOCCIDA,
          A.MOTIVO_SOCCIDA,
          A.ID_ALLEVAMENTO,
          A.ID_ASL,
          A.ID_ORIENTAMENTO_PRODUTTIVO,
          TOP.DESCRIZIONE AS DESC_ORIENTAMENTO_PRODUTTIVO,
          A.LATITUDINE,
          A.LONGITUDINE,
          A.ID_TIPO_PRODUZIONE,
          TTP.DESCRIZIONE AS DESC_TIPO_PRODUZIONE,
          A.ID_TIPO_PRODUZIONE_COSMAN AS DESC_TIPO_PRODUZIONE_COSMAN,
          TPC.DESCRIZIONE,
          A.NOTE,
          A.TELEFONO,
          A.ISTAT_COMUNE,
          A.INDIRIZZO,
          A.CAP,
          A.ID_UTE,
          CA.ID_CATEGORIE_ALLEVAMENTO,
          CA.ID_CATEGORIA_ANIMALE,
          TCA.DESCRIZIONE AS DESCRIZIONE_CATEGORIA_ANIMALE,
          CA.PESO_VIVO_UNITARIO,
          SA.ID_SOTTOCATEGORIA_ANIMALE,
          TSA.DESCRIZIONE AS DESCRIZIONE_SOTTOCATEG_ANIMALE,
          SA.QUANTITA,
          SA.QUANTITA_PROPRIETA,
          SA.FLAG_BIOLOGICO,
          SA.GIORNI_PASCOLO_ESTATE,
          SA.GIORNI_PASCOLO_INVERNO,
          SA.ORE_PASCOLO_ESTATE,
          SA.ORE_PASCOLO_INVERNO,
          SA.GIORNI_VUOTO_SANITARIO,
          SA.NUMERO_CICLI_ANNUALI,
          SA.PESO_VIVO,
          dc.ID_DICHIARAZIONE_CONSISTENZA,
          sa.ID_SOTTOCATEGORIA_ALLEVAMENTO,
          TSPA.FLAG_PASCOLABILE
     FROM DB_ALLEVAMENTO A,
          DB_CATEGORIE_ALLEVAMENTO CA,
          DB_SOTTOCATEGORIA_ALLEVAMENTO SA,
          DB_TIPO_SOTTOCATEGORIA_ANIMALE TSA,
          DB_TIPO_CATEGORIA_ANIMALE TCA,
          DB_DICHIARAZIONE_CONSISTENZA DC,
          DB_ANAGRAFICA_AZIENDA AA,
          DB_UTE U,
          DB_TIPO_SPECIE_ANIMALE TSPA,
          DB_TIPO_ORIENTAMENTO_PRODUT TOP,
          DB_TIPO_PRODUZIONE_COSMAN TPC,
          DB_TIPO_TIPO_PRODUZIONE TTP
    WHERE     DC.ID_AZIENDA = AA.ID_AZIENDA
          AND AA.DATA_FINE_VALIDITA IS NULL
          AND AA.ID_AZIENDA = U.ID_AZIENDA
          AND DC.DATA_INSERIMENTO_DICHIARAZIONE BETWEEN U.DATA_INIZIO_ATTIVITA
                                                    AND NVL (
                                                           U.DATA_FINE_ATTIVITA,
                                                           SYSDATE + 1)
          AND U.ID_UTE = A.ID_UTE
          AND DC.DATA_INSERIMENTO_DICHIARAZIONE BETWEEN A.DATA_INIZIO
                                                    AND NVL (A.DATA_FINE,
                                                             SYSDATE + 1)
          AND A.ID_ALLEVAMENTO = CA.ID_ALLEVAMENTO
          AND CA.ID_CATEGORIE_ALLEVAMENTO = SA.ID_CATEGORIE_ALLEVAMENTO
          AND TSA.ID_SOTTOCATEGORIA_ANIMALE = SA.ID_SOTTOCATEGORIA_ANIMALE
          AND A.ID_SPECIE_ANIMALE = TSPA.ID_SPECIE_ANIMALE
          AND TCA.ID_CATEGORIA_ANIMALE = CA.ID_CATEGORIA_ANIMALE
          AND TOP.ID_ORIENTAMENTO_PRODUTTIVO(+) =
                 A.ID_ORIENTAMENTO_PRODUTTIVO
          AND TPC.ID_TIPO_PRODUZIONE_COSMAN(+) = A.ID_TIPO_PRODUZIONE_COSMAN
          AND TTP.ID_TIPO_PRODUZIONE(+) = A.ID_TIPO_PRODUZIONE
