  SELECT aa.ID_AZIENDA,
          aa.DATA_INIZIO_VALIDITA,
          aa.DATA_FINE_VALIDITA,
          aa.CUAA,
          aa.PARTITA_IVA,
          aa.DENOMINAZIONE,
          aa.INTESTAZIONE_PARTITA_IVA,
          aa.ID_FORMA_GIURIDICA,
          (SELECT DESCRIZIONE
             FROM DB_TIPO_FORMA_GIURIDICA TFG
            WHERE TFG.ID_FORMA_GIURIDICA = AA.ID_FORMA_GIURIDICA)
             DESCRIZIONE_FORMA_GIURIDICA,
          aa.ID_ATTIVITA_ATECO,
          (SELECT CODICE
             FROM DB_TIPO_ATTIVITA_ATECO TAA
            WHERE TAA.ID_ATTIVITA_ATECO = AA.ID_ATTIVITA_ATECO)
             CODICE_ATTIVITA_ATECO,
          (SELECT DESCRIZIONE
             FROM DB_TIPO_ATTIVITA_ATECO TAA
            WHERE TAA.ID_ATTIVITA_ATECO = AA.ID_ATTIVITA_ATECO)
             DESCRIZIONE_ATTIVITA_ATECO,
          (SELECT CODICE
             FROM DB_TIPO_ATTIVITA_OTE TAO
            WHERE TAO.ID_ATTIVITA_OTE = AA.ID_ATTIVITA_OTE)
             CODICE_ATTIVITA_OTE,
          aa.MAIL,
          aa.TELEFONO,
          aa.FAX,
          aa.PEC,
          AA.SEDELEG_COMUNE ISTAT_COMUNE_SEDE_LEGALE,
          CASE
             WHEN aa.SEDELEG_CITTA_ESTERO IS NULL
             THEN
                   aa.SEDELEG_INDIRIZZO
                || ' - '
                || aa.SEDELEG_CAP
                || ' - '
                || (SELECT DESCOM || ' (' || SIGLA_PROVINCIA || ')'
                      FROM COMUNE C, PROVINCIA P
                     WHERE     AA.SEDELEG_COMUNE = C.ISTAT_COMUNE
                           AND C.ISTAT_PROVINCIA = P.ISTAT_PROVINCIA)
             ELSE
                   aa.SEDELEG_INDIRIZZO
                || ' - '
                || aa.SEDELEG_CITTA_ESTERO
                || (SELECT ' (' || DESCOM || ')'
                      FROM COMUNE C
                     WHERE AA.SEDELEG_COMUNE = C.ISTAT_COMUNE)
          END
             INDIRIZZO_SEDE_LEGALE,
          aa.DATA_CESSAZIONE,
          (SELECT DESCRIZIONE
             FROM DB_TIPO_CESSAZIONE TC
            WHERE TC.ID_CESSAZIONE = AA.ID_CESSAZIONE)
             MOTIVO_CESSAZIONE,
          aa.CCIAA_ANNO_ISCRIZIONE,
          aa.CCIAA_NUMERO_REA,
          aa.CCIAA_PROVINCIA_REA,
          aa.CCIAA_NUMERO_REGISTRO_IMPRESE,
          aa.ID_TIPOLOGIA_AZIENDA,
          (SELECT DESCRIZIONE
             FROM DB_TIPO_TIPOLOGIA_AZIENDA TTA
            WHERE TTA.ID_TIPOLOGIA_AZIENDA = AA.ID_TIPOLOGIA_AZIENDA)
             TIPOLOGIA_AZIENDA,
          aa.ULU,
          aa.RLS,
          aa.ID_UDE,
          aa.ESONERO_PAGAMENTO_GF,
          aa.CODICE_AGRITURISMO,
          aa.FLAG_IAP,
          aa.SEDELEG_CAP,
          aa.SEDELEG_INDIRIZZO,
          aa.SEDELEG_CITTA_ESTERO,
          A.ID_AZIENDA_PROVENIENZA,
          (    SELECT MAX (codice_originale)
                 FROM DB_TIPO_ATTIVITA_ATECO
           CONNECT BY id_attivita_ateco_padre = PRIOR id_attivita_ateco
           START WITH id_attivita_ateco = AA.ID_ATTIVITA_ATECO)
             COD_ORIGINALE_ATTIVITA_ATECO,
          (    SELECT codice
                 FROM DB_TIPO_ATTIVITA_ATECO
                WHERE id_attivita_ateco_padre IS NULL
           CONNECT BY id_attivita_ateco = PRIOR id_attivita_ateco_padre
           START WITH id_attivita_ateco = AA.ID_ATTIVITA_ATECO)
             CODICE_PADRE_ATTIVITA_ATECO
     FROM DB_ANAGRAFICA_AZIENDA AA, DB_AZIENDA A
    WHERE AA.ID_AZIENDA = A.ID_AZIENDA
