  CREATE OR REPLACE FORCE VIEW "SMRGAA"."SMRGAA_V_PRATICHE" ("CODICE", "ID_AZIENDA", "EXT_ID_PROCEDIMENTO", "ID_DICHIARAZIONE_CONSISTENZA", "DATA_TRASMISSIONE_PRATICA", "ID_STATO_PRATICA_ATTUALE", "DESCR_STATO_PRATICA_ATTUALE", "ANNO_CAMPAGNA", "UTENTE_PRESENTAZIONE_DOMANDA", "DENOMINAZIONE_BANDO", "DESCRIZIONE_TIPO_LIVELLO", "IDENTIFICATIVO_DOMANDA") AS 
  SELECT DLV.CODICE,
          AAZ.ID_AZIENDA,
          PRA.EXT_ID_PROCEDIMENTO,
          PRA.EXT_ID_CONSISTENZA AS ID_DICHIARAZIONE_CONSISTENZA,
          (SELECT MAX (TIP2.DATA_OPERAZIONE)
             FROM RPU_T_ITER_PRATICA TIP2
            WHERE     PRA.ID_PRATICA = TIP2.ID_PRATICA
                  AND TIP2.ID_STATO_PRATICA = 20)
             AS DATA_TRASMISSIONE_PRATICA,
          TIP.ID_STATO_PRATICA AS ID_STATO_PRATICA_ATTUALE,
          DSP.DESCRIZIONE AS DESCR_STATO_PRATICA_ATTUALE,
          DAI.ANNO_CAMPAGNA,
          (SELECT TIP2.EXT_ID_UTENTE
             FROM RPU_T_ITER_PRATICA TIP2
            WHERE     PRA.ID_PRATICA = TIP2.ID_PRATICA
                  AND TIP2.ID_STATO_PRATICA = 20
                  AND TIP2.DATA_OPERAZIONE =
                         (SELECT MAX (TIP3.DATA_OPERAZIONE)
                            FROM RPU_T_ITER_PRATICA TIP3
                           WHERE     PRA.ID_PRATICA = TIP3.ID_PRATICA
                                 AND TIP3.ID_STATO_PRATICA = 20))
             UTENTE_PRESENTAZIONE_DOMANDA,
          DAI.DENOMINAZIONE DENOMINAZIONE_BANDO,
          NULL DESCRIZIONE_TIPO_LIVELLO,
          pra.identificativo identificativo_domanda
     FROM RPU_T_PRATICA PRA,
          RPU_T_ITER_PRATICA TIP,
          RPU_R_LIVELLO_AIUTO RLA,
          RPU_D_LIVELLO DLV,
          RPU_D_AIUTO DAI,
          RPU_D_STATO_PRATICA DSP,
          DB_DICHIARAZIONE_CONSISTENZA DIC,
          DB_ANAGRAFICA_AZIENDA AAZ
    WHERE     PRA.ID_LIVELLO_AIUTO = RLA.ID_LIVELLO_AIUTO
          AND DAI.ID_AIUTO = RLA.ID_AIUTO
          AND PRA.ID_PRATICA = TIP.ID_PRATICA
          AND TIP.ID_STATO_PRATICA IN (20, 60)
          AND TIP.DATA_FINE IS NULL
          AND DLV.ID_LIVELLO = RLA.ID_LIVELLO
          AND DSP.ID_STATO_PRATICA = TIP.ID_STATO_PRATICA
          AND PRA.EXT_ID_CONSISTENZA = DIC.ID_DICHIARAZIONE_CONSISTENZA
          AND AAZ.ID_AZIENDA = DIC.ID_AZIENDA
          AND AAZ.DATA_FINE_VALIDITA IS NULL
   UNION
   SELECT DLV.CODICE,
          AAZ.ID_AZIENDA,
          PRA.EXT_ID_PROCEDIMENTO,
          PRA.EXT_ID_CONSISTENZA AS ID_DICHIARAZIONE_CONSISTENZA,
          (SELECT MAX (TIP2.DATA_OPERAZIONE)
             FROM GNPS_T_ITER_PRATICA TIP2
            WHERE     PRA.ID_PRATICA = TIP2.ID_PRATICA
                  AND TIP2.ID_STATO_PRATICA = 20)
             AS DATA_TRASMISSIONE_PRATICA,
          TIP.ID_STATO_PRATICA AS ID_STATO_PRATICA_ATTUALE,
          DSP.DESCRIZIONE AS DESCR_STATO_PRATICA_ATTUALE,
          PRA.ANNO_PRATICA ANNO_CAMPAGNA,
          (SELECT TIP2.EXT_ID_UTENTE
             FROM GNPS_T_ITER_PRATICA TIP2
            WHERE     PRA.ID_PRATICA = TIP2.ID_PRATICA
                  AND TIP2.ID_STATO_PRATICA = 20
                  AND TIP2.DATA_OPERAZIONE =
                         (SELECT MAX (TIP3.DATA_OPERAZIONE)
                            FROM GNPS_T_ITER_PRATICA TIP3
                           WHERE     PRA.ID_PRATICA = TIP3.ID_PRATICA
                                 AND TIP3.ID_STATO_PRATICA = 20))
             UTENTE_PRESENTAZIONE_DOMANDA,
          DAI.DENOMINAZIONE DENOMINAZIONE_BANDO,
          NULL DESCRIZIONE_TIPO_LIVELLO,
          pra.identificativo identificativo_domanda
     FROM GNPS_T_PRATICA PRA,
          GNPS_T_ITER_PRATICA TIP,
          GNPS_R_LIVELLO_AIUTO RLA,
          GNPS_D_AIUTO DAI,
          GNPS_D_LIVELLO DLV,
          GNPS_D_STATO_PRATICA DSP,
          DB_DICHIARAZIONE_CONSISTENZA DIC,
          DB_ANAGRAFICA_AZIENDA AAZ
    WHERE     PRA.ID_LIVELLO_AIUTO = RLA.ID_LIVELLO_AIUTO
          AND RLA.ID_AIUTO = DAI.ID_AIUTO
          AND RLA.ID_LIVELLO = DLV.ID_LIVELLO
          AND EXISTS
                 (SELECT *
                    FROM GNPS_T_ITER_PRATICA TIP
                   WHERE     PRA.ID_PRATICA = TIP.ID_PRATICA
                         AND TIP.ID_STATO_PRATICA = 20)
          AND NOT EXISTS
                     (SELECT *
                        FROM GNPS_T_ITER_PRATICA TIP
                       WHERE     PRA.ID_PRATICA = TIP.ID_PRATICA
                             AND TIP.ID_STATO_PRATICA IN (50, 55))
          AND PRA.ID_PRATICA = TIP.ID_PRATICA
          AND TIP.DATA_FINE IS NULL
          AND DSP.ID_STATO_PRATICA = TIP.ID_STATO_PRATICA
          AND PRA.EXT_ID_CONSISTENZA = DIC.ID_DICHIARAZIONE_CONSISTENZA
          AND AAZ.ID_AZIENDA = PRA.EXT_ID_AZIENDA
          AND AAZ.DATA_FINE_VALIDITA IS NULL
   UNION
   SELECT LV.CODICE_LIVELLO,
          PA.EXT_ID_AZIENDA,
          46 EXT_ID_PROCEDIMENTO,
          PO.EXT_ID_DICHIARAZIONE_CONSISTEN ID_DICHIARAZIONE_CONSISTENZA,
          IPO.DATA_INIZIO DATA_TRASMISSIONE_PRATICA,
          P.ID_STATO_OGGETTO ID_STATO_PRATICA_ATTUALE,
          SO.DESCRIZIONE AS DESCR_STATO_PRATICA_ATTUALE,
          b.anno_campagna,
          IPO.EXT_ID_UTENTE_AGGIORNAMENTO UTENTE_PRESENTAZIONE_DOMANDA,
          B.DENOMINAZIONE DENOMINAZIONE_BANDO,
          NVL (TL.DESCRIZIONE, 'Psl') DESCRIZIONE_TIPO_LIVELLO,
          p.identificativo identificativo_domanda
     FROM PSR_T_PROCEDIMENTO P,
          PSR_T_PROCEDIMENTO_OGGETTO PO,
          PSR_T_ITER_PROCEDIMENTO_OGGETT IPO,
          PSR_D_BANDO B,
          PSR_D_STATO_OGGETTO SO,
          PSR_T_PROCEDIMENTO_AZIENDA PA,
          PSR_R_PROCEDIMENTO_LIVELLO PL,
          PSR_D_LIVELLO LV,
          PSR_D_TIPO_LIVELLO TL
    WHERE     P.ID_PROCEDIMENTO = PO.ID_PROCEDIMENTO
          AND P.ID_STATO_OGGETTO BETWEEN 10 AND 90
          AND P.ID_BANDO = B.ID_BANDO
          AND PL.ID_PROCEDIMENTO = P.ID_PROCEDIMENTO
          AND LV.ID_LIVELLO = PL.ID_LIVELLO
          AND TL.ID_TIPO_LIVELLO = B.ID_TIPO_LIVELLO
          AND SO.ID_STATO_OGGETTO = P.ID_STATO_OGGETTO
          AND po.ID_PROCEDIMENTO_OGGETTO =
                 (SELECT MIN (po_max.id_procedimento_oggetto)
                    FROM PSR_T_PROCEDIMENTO_OGGETTO po_max
                   WHERE     po_max.id_procedimento = p.ID_PROCEDIMENTO
                         AND po_max.DATA_FINE IS NOT NULL
                         AND po_max.ID_ESITO = 4)
          AND PA.ID_PROCEDIMENTO = P.ID_PROCEDIMENTO
          AND PA.DATA_FINE IS NULL
          AND IPO.ID_PROCEDIMENTO_OGGETTO = PO.ID_PROCEDIMENTO_OGGETTO
          AND IPO.ID_STATO_OGGETTO = 10
;
