  SELECT CO.ID_AZIENDA,
          CO.ID_RUOLO,
          TR.DESCRIZIONE DESCRIZIONE_RUOLO,
          CO.DATA_INIZIO_RUOLO AS DATA_INIZIO_VALIDITA,
          CO.DATA_FINE_RUOLO AS DATA_FINE_VALIDITA,
          CO.DATA_INIZIO_RUOLO_mod AS DATA_INIZIO_RUOLO,
          CO.DATA_FINE_RUOLO_mod AS DATA_FINE_RUOLO,
          PF.CODICE_FISCALE,
          PF.COGNOME,
          PF.NOME,
          PF.SESSO,
          PF.NASCITA_DATA,
          PF.NUMERO_CELLULARE,
          PF.ID_TITOLO_STUDIO,
          (SELECT TTS.DESCRIZIONE
             FROM DB_TIPO_TITOLO_STUDIO TTS
            WHERE TTS.ID_TITOLO_STUDIO = PF.ID_TITOLO_STUDIO)
             DESCRIZIONE_TITOLO_STUDIO,
          PF.ID_INDIRIZZO_STUDIO,
          PF.RES_TELEFONO,
          PF.RES_MAIL,
          PF.RES_FAX,
          CASE
             WHEN PF.NASCITA_CITTA_ESTERO IS NULL
             THEN
                (SELECT DESCOM || ' (' || SIGLA_PROVINCIA || ')'
                   FROM COMUNE C, PROVINCIA P
                  WHERE     PF.NASCITA_COMUNE = C.ISTAT_COMUNE
                        AND C.ISTAT_PROVINCIA = P.ISTAT_PROVINCIA)
             ELSE
                   RTRIM (PF.NASCITA_CITTA_ESTERO)
                || (SELECT ' (' || DESCOM || ')'
                      FROM COMUNE C
                     WHERE PF.NASCITA_COMUNE = C.ISTAT_COMUNE)
          END
             COMUNE_NASCITA,
          CASE
             WHEN RES_CITTA_ESTERO IS NULL
             THEN
                   RES_INDIRIZZO
                || ' - '
                || RES_CAP
                || ' - '
                || (SELECT DESCOM || ' (' || SIGLA_PROVINCIA || ')'
                      FROM COMUNE C, PROVINCIA P
                     WHERE     RES_COMUNE = C.ISTAT_COMUNE
                           AND C.ISTAT_PROVINCIA = P.ISTAT_PROVINCIA)
             ELSE
                   RES_INDIRIZZO
                || ' - '
                || RES_CITTA_ESTERO
                || (SELECT ' (' || DESCOM || ')'
                      FROM COMUNE C
                     WHERE RES_COMUNE = C.ISTAT_COMUNE)
          END
             INDIRIZZO_RESIDENZA,
          CO.ID_SOGGETTO,
          S.FLAG_FISICO,
          CO.ID_CONTITOLARE,
          TR.FLAG_POTERE_FIRMA,
          PF.RES_COMUNE,
          RES_CAP,
          RES_INDIRIZZO,
          RES_CITTA_ESTERO,
          PF.NASCITA_COMUNE
     FROM DB_CONTITOLARE CO,
          DB_PERSONA_FISICA PF,
          DB_TIPO_RUOLO TR,
          DB_SOGGETTO S
    WHERE     CO.ID_SOGGETTO = PF.ID_SOGGETTO
          AND CO.ID_RUOLO = TR.ID_RUOLO
          AND S.ID_SOGGETTO = CO.ID_SOGGETTO
;
