  CREATE OR REPLACE FORCE VIEW "SMRGAA"."UTENTE_IRIDE_ACCESSO" ("ID_UTENTE", "DENOMINAZIONE", "CODICE_FISCALE", "MAIL", "DATA_ULTIMO_ACCESSO", "NUMERO_ACCESSI", "ID_PROFILO", "PROVINCIA", "ID_INTERMEDIARIO", "CUAA", "DESCRIZIONE_ENTE_APPARTENENZA", "ID_PROCEDIMENTO", "CODICE_ENTE") AS 
  (SELECT UI.ID_UTENTE_IRIDE2 AS ID_UTENTE,
           UI.DENOMINAZIONE AS DENOMINAZIONE,
           UI.CODICE_FISCALE AS CODICE_FISCALE,
           UI.EMAIL AS MAIL,
           UI.DATA_ULTIMO_ACCESSO AS DATA_ULTIMO_ACCESSO,
           UI.NUMERO_ACCESSI AS NUMERO_ACCESSI,
           PRI.ID_PROFILO,
              FUNZ_PROV.SIGLA_PROVINCIA
           || TAB_OPR.SIGLA_PROVINCIA
           || TAB_COMMONT.SIGLA_PROVINCIA
              AS PROVINCIA,
           INTER_OPR_GE.ID_INTERMEDIARIO AS ID_INTERMEDIARIO,
           TAB_AZ.CUAA AS CUAA,
              FUNZ_REG.DESCRIZIONE_ENTE_APPARTENENZA
           || FUNZ_PROV.DESCRIZIONE_ENTE_APPARTENENZA
           || INTER_OPR_GE.DESCRIZIONE_ENTE_APPARTENENZA
           || TAB_AZ.DESCRIZIONE_ENTE_APPARTENENZA
           || TAB_OPR.DESCRIZIONE_ENTE_APPARTENENZA
           || TAB_COMMONT.DESCRIZIONE_ENTE_APPARTENENZA
           || TAB_TIT_LEG.DESCRIZIONE_ENTE_APPARTENENZA
           || TAB_ALTRI.DESCRIZIONE_ENTE_APPARTENENZA
           || TAB_ODC.DESCRIZIONE_ENTE_APPARTENENZA
           || TAB_FUNZ_REGIONE.DESCRIZIONE_ENTE_APPARTENENZA
              AS DESCRIZIONE_ENTE_APPARTENENZA,
           UI.EXT_ID_PROCEDIMENTO,
              NVL (FUNZ_REG.CODICE_ENTE, '')
           || NVL (TAB_OPR.CODICE_ENTE, '')
           || NVL (TAB_COMMONT.CODICE_ENTE, '')
           || NVL (TAB_TIT_LEG.CODICE_ENTE, '')
           || NVL (TAB_ALTRI.CODICE_ENTE, '')
           || NVL (TAB_FUNZ_REGIONE.CODICE_ENTE, '')
           || NVL (TAB_ODC.CODICE_ENTE, '')
              AS CODICE_ENTE
      FROM (SELECT UI2.ID_UTENTE_IRIDE2,
                   NULL AS CODICE_ENTE,
                   'REGIONE ' || RE.DESCRIZIONE
                      AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, REGIONE RE
             WHERE     UI2.CODICE_ENTE = RE.ID_REGIONE
                   AND (   UI2.RUOLO = 'FUNZIONARIO@REGIONE_AGRI'
                        OR UI2.RUOLO = 'FUNZIONARIO@REG_PMN_AGRI')) FUNZ_REG,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   PR.SIGLA_PROVINCIA AS SIGLA_PROVINCIA,
                   PR.DESCRIZIONE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, PROVINCIA PR
             WHERE     UI2.CODICE_ENTE = PR.ISTAT_PROVINCIA
                   AND UI2.RUOLO = 'FUNZIONARIO@PROVINCIA_AGRI') FUNZ_PROV,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   INT.ID_INTERMEDIARIO AS ID_INTERMEDIARIO,
                   INT.DENOMINAZIONE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, DB_INTERMEDIARIO INT
             WHERE     UI2.CODICE_ENTE = INT.CODICE_FISCALE
                   AND UI2.RUOLO IN
                          ('OPR_GESTORE@AGRICOLTURA',
                           'INTERMEDIARIO@AGRICOLTURA',
                           'GESTORE_CAA@AGRICOLTURA')) INTER_OPR_GE,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   CODICE_ENTE AS CUAA,
                   AN.DENOMINAZIONE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, SMRGAA.DB_ANAGRAFICA_AZIENDA AN
             WHERE     UI2.CODICE_ENTE = AN.CUAA
                   AND AN.DATA_FINE_VALIDITA IS NULL
                   AND (UI2.RUOLO = 'AZIENDA_AGRICOLA@AGRICOLTURA')) TAB_AZ,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   'TO' AS SIGLA_PROVINCIA,
                   UI2.CODICE_ENTE AS CODICE_ENTE,
                   UI2.CODICE_ENTE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2
             WHERE (UI2.RUOLO = 'OPR@AGRICOLTURA' or UI2.RUOLO = 'OPR_ISTRUTTORE@AGRICOLTURA')) TAB_OPR,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   PR.SIGLA_PROVINCIA AS SIGLA_PROVINCIA,
                   UI2.CODICE_ENTE AS CODICE_ENTE,
                   AM.DESCRIZIONE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, DB_AMM_COMPETENZA AM, PROVINCIA PR
             WHERE     UI2.CODICE_ENTE = AM.CODICE_AMMINISTRAZIONE
                   AND PR.ISTAT_PROVINCIA = SUBSTR (UI2.CODICE_ENTE, 0, 3)
                   AND UI2.RUOLO = 'FUNZIONARIO@COM_MONTANA_AGRI'
                   AND UI2.CODICE_ENTE IS NOT NULL) TAB_COMMONT,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   UI2.CODICE_FISCALE AS CODICE_ENTE,
                   UI2.DENOMINAZIONE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2
             WHERE     (   UI2.RUOLO = 'TITOLARE_CF@UTENTI_IRIDE2'
                        OR UI2.RUOLO = 'LEGALE_RAPPRESENTANTE@ALI')
                   AND UI2.CODICE_ENTE IS NOT NULL) TAB_TIT_LEG,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   NULL AS CODICE_ENTE,
                   'REGIONE ' || RE.DESCRIZIONE
                      AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, REGIONE RE
             WHERE     UI2.CODICE_ENTE = RE.ID_REGIONE
                   AND UI2.RUOLO IN
                          ('FUNZIONARIO@REG_PMN_ASS_AGRI',
                           'FUNZIONARIO@REG_SARDEGNA_AGRI')) TAB_FUNZ_REGIONE,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   UI2.CODICE_ENTE AS CODICE_ENTE,
                   AM.DESCRIZIONE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, DB_AMM_COMPETENZA AM
             WHERE     UI2.CODICE_ENTE = AM.CODICE_AMMINISTRAZIONE
                   AND UI2.RUOLO IN
                          ('TECNICO@AGRICOLTURA',
                           'FUNZIONARIO@GUARDIA_DI_FINANZA',
                           'FUNZIONARIO@INPS',
                           'SAV@PROVINCIA_AGRI',
                           'PA_CONTROLLORE@AGRICOLTURA',
                           'SERVIZI_ASSISTENZA@AGRICOLTURA',
                           'FUNZIONARIO@ENTI_LOCALI',
                           'FUNZIONARIO@COMUNE_AGRI',
                           'FUNZIONARIO_SU@ENTI_LOCALI',
                           'DISTRIBUTORE@AGRICOLTURA',
                           'MONITORAGGIO@AGRICOLTURA',
                           'VETERINARIO_AGRICOLTURA@ASL',
                           'ENTE_DELEGATO@AGRICOLTURA',
                           'ASSISTENZA_CSI@AGRICOLTURA')
                   AND UI2.CODICE_ENTE IS NOT NULL) TAB_ALTRI,
           (SELECT UI2.ID_UTENTE_IRIDE2,
                   UI2.CODICE_ENTE AS CODICE_ENTE,
                   EP.DENOMINAZIONE_ENTE AS DESCRIZIONE_ENTE_APPARTENENZA
              FROM DB_UTENTE_IRIDE2 UI2, DB_D_ENTE_PRIVATO EP
             WHERE     UI2.CODICE_ENTE = EP.CODICE_ENTE_PRIVATO
                   AND UI2.RUOLO = 'ODC@AGRICOLTURA'
                   AND UI2.CODICE_ENTE IS NOT NULL) TAB_ODC,
           DB_UTENTE_IRIDE2 UI,
           DB_R_PROFILO_RUOLO_IRIDE PRI
     WHERE     UI.ID_UTENTE_IRIDE2 = FUNZ_REG.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = TAB_ODC.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = TAB_ALTRI.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = TAB_FUNZ_REGIONE.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = TAB_TIT_LEG.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = TAB_COMMONT.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = TAB_OPR.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = TAB_AZ.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = INTER_OPR_GE.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = FUNZ_PROV.ID_UTENTE_IRIDE2(+)
           AND UI.ID_UTENTE_IRIDE2 = FUNZ_REG.ID_UTENTE_IRIDE2(+)
           AND UI.RUOLO <> '-'
           AND UI.EXT_ID_PROCEDIMENTO IN (1, 7, 18)
           AND UI.RUOLO = PRI.RUOLO
           AND UI.EXT_ID_PROCEDIMENTO = PRI.EXT_ID_PROCEDIMENTO
           AND UI.DIRITTO_ACCESSO = PRI.DIRITTO_ACCESSO)
;
